function setLanguage(lang) {localStorage.setItem('lang',lang); window.location.href = `/${lang}/index.html`;}function translatePage(lang) {const elements = document.querySelectorAll('[data-i18n]'); elements.forEach(el => {const key = el.getAttribute('data-i18n'); if (translations[lang] && translations[lang][key]) {el.innerHTML = translations[lang][key];}});}function detectBrowserLanguage() {const supported = Object.keys(translations); const browserLang = navigator.language.slice(0,2).toLowerCase(); return supported.includes(browserLang) ? browserLang :'en';}document.addEventListener("DOMContentLoaded",() => {const supported = ['en','hu','de','fr','nl','es']; const savedLang = localStorage.getItem('lang'); const currentLang = window.location.pathname.split('/')[1]; if (!savedLang && currentLang) {setLanguage(currentLang);} if (savedLang && supported.includes(savedLang) && savedLang !== currentLang) {window.location.href = `/${savedLang}${window.location.pathname.replace(/^\/[^/]+/,'')}`;}});